CREATE TRIGGER `trg_attendance_total_minutes` BEFORE INSERT ON `attendance` FOR EACH ROW BEGIN
    -- minute counters
    DECLARE morning_minutes INT DEFAULT 0;
    DECLARE afternoon_minutes INT DEFAULT 0;
    DECLARE total_minutes INT DEFAULT 0;

    -- shift reference and times
    DECLARE shift_id INT DEFAULT NULL;
    DECLARE wt_start TIME;
    DECLARE wt_end TIME;
    DECLARE shift_start_dt DATETIME;
    DECLARE shift_end_dt DATETIME;

    -- temporary datetimes for in/out
    DECLARE in_dt DATETIME;
    DECLARE out_dt DATETIME;

    -- 1) find assigned shift id from employee_shift_schedule (if any)
    SELECT work_time_id
      INTO shift_id
    FROM employee_shift_schedule
    WHERE employee_id = NEW.employee_id
      AND is_active = 1
      AND effective_date <= NEW.attendance_date
      AND (end_date IS NULL OR end_date >= NEW.attendance_date)
    ORDER BY priority DESC, effective_date DESC
    LIMIT 1;

    -- 2) fetch work_time row (will be NULL if shift_id is NULL or not found)
    IF shift_id IS NOT NULL THEN
        SELECT start_time, end_time
          INTO wt_start, wt_end
        FROM work_time
        WHERE id = shift_id
        LIMIT 1;
    ELSE
        SET wt_start = NULL;
        SET wt_end = NULL;
    END IF;

    -- 3) compute shift start/end datetimes (handle overnight shifts)
    IF wt_start IS NOT NULL AND wt_end IS NOT NULL THEN
        SET shift_start_dt = CAST(CONCAT(NEW.attendance_date, ' ', wt_start) AS DATETIME);
        IF wt_end <= wt_start THEN
            -- shift ends next day
            SET shift_end_dt = CAST(CONCAT(DATE_ADD(NEW.attendance_date, INTERVAL 1 DAY), ' ', wt_end) AS DATETIME);
        ELSE
            SET shift_end_dt = CAST(CONCAT(NEW.attendance_date, ' ', wt_end) AS DATETIME);
        END IF;
    ELSE
        SET shift_start_dt = NULL;
        SET shift_end_dt = NULL;
    END IF;

    -- 4) calculate morning minutes (only if both times present)
    IF NEW.time_in_morning IS NOT NULL AND NEW.time_out_morning IS NOT NULL THEN
        SET in_dt  = CAST(CONCAT(NEW.attendance_date, ' ', NEW.time_in_morning) AS DATETIME);
        SET out_dt = CAST(CONCAT(NEW.attendance_date, ' ', NEW.time_out_morning) AS DATETIME);

        -- if shift window exists, clip in/out to shift_start_dt..shift_end_dt
        IF shift_start_dt IS NOT NULL THEN
            IF in_dt < shift_start_dt THEN SET in_dt = shift_start_dt; END IF;
            IF out_dt > shift_end_dt THEN SET out_dt = shift_end_dt; END IF;
        END IF;

        -- compute difference only if in < out
        IF in_dt < out_dt THEN
            SET morning_minutes = TIMESTAMPDIFF(MINUTE, in_dt, out_dt);
        ELSE
            SET morning_minutes = 0;
        END IF;
    END IF;

    -- 5) calculate afternoon minutes (only if both times present)
    IF NEW.time_in_afternoon IS NOT NULL AND NEW.time_out_afternoon IS NOT NULL THEN
        SET in_dt  = CAST(CONCAT(NEW.attendance_date, ' ', NEW.time_in_afternoon) AS DATETIME);
        SET out_dt = CAST(CONCAT(NEW.attendance_date, ' ', NEW.time_out_afternoon) AS DATETIME);

        -- if shift window exists, clip in/out to shift_start_dt..shift_end_dt
        IF shift_start_dt IS NOT NULL THEN
            IF in_dt < shift_start_dt THEN SET in_dt = shift_start_dt; END IF;
            IF out_dt > shift_end_dt THEN SET out_dt = shift_end_dt; END IF;
        END IF;

        -- compute difference only if in < out
        IF in_dt < out_dt THEN
            SET afternoon_minutes = TIMESTAMPDIFF(MINUTE, in_dt, out_dt);
        ELSE
            SET afternoon_minutes = 0;
        END IF;
    END IF;

    -- 6) total and store (DO NOT touch days_credited)
    SET total_minutes = morning_minutes + afternoon_minutes;
    SET NEW.actual_rendered_minutes = total_minutes;
    SET NEW.total_rendered_hours = ROUND(total_minutes / 60.0, 2);

    -- attach the shift id (nullable)
    SET NEW.work_time_id = shift_id;
END
$$
