CREATE TRIGGER `after_leave_request_status_update` AFTER UPDATE ON `leave_requests` FOR EACH ROW BEGIN
    -- If approved and was not approved before
    IF NEW.status = 'approved' AND OLD.status <> 'approved' THEN
        INSERT INTO employee_leaves (
            employee_id,
            leave_type_id,
            date_from,
            date_until,
            total_days,
            reason,
            status,
            created_at,
            updated_at
        )
        VALUES (
            NEW.employee_id,
            NEW.leave_type_id,
            NEW.date_from,
            NEW.date_until,
            COALESCE(NEW.total_days, 1),
            NEW.reason,
            'approved',
            NOW(),
            NOW()
        );
    END IF;

    -- If rejected and was not rejected before
    IF NEW.status = 'rejected' AND OLD.status <> 'rejected' THEN
        INSERT INTO leave_deduction_logs (
            employee_id,
            leave_type_id,
            leave_id,
            deducted_days
        )
        VALUES (
            NEW.employee_id,
            NEW.leave_type_id,
            NEW.leave_id,
            0
        );
    END IF;
END
$$
