CREATE TRIGGER `trg_update_final_loan_deduction` BEFORE UPDATE ON `loans` FOR EACH ROW BEGIN
  -- Only process if NEW deduction value is provided and different
  IF NEW.final_loan_deduction IS NOT NULL 
     AND NEW.final_loan_deduction > 0 
     AND (OLD.final_loan_deduction IS NULL OR OLD.final_loan_deduction != NEW.final_loan_deduction)
  THEN
    -- Step 1: Subtract from balance
    SET NEW.balance = GREATEST(OLD.balance - NEW.final_loan_deduction, 0);

